import javax.crypto.Cipher;
import javax.crypto.spec.IvParameterSpec;
import javax.crypto.spec.SecretKeySpec;
import java.nio.charset.StandardCharsets;
import java.security.MessageDigest;
import java.util.Base64;

public class CryptoTest {

    public static void main(String[] args) throws Exception {

        // === 1. INPUTS (paste real values) ===
        String encryptedBase64 = "";
        String hashA = "1262765";
        String hashB = "1262765";

        // === 2. Seed ===
        String seed = Base64.getEncoder()
                .encodeToString((hashA + hashB).getBytes(StandardCharsets.UTF_8))
                .substring(0, 16);

        // === 3. SHA-256 ===
        byte[] sha = MessageDigest
                .getInstance("SHA-256")
                .digest(seed.getBytes(StandardCharsets.UTF_8));

        byte[] keyBytes = new byte[16];
        System.arraycopy(sha, 0, keyBytes, 0, 16);

        // === 4. AES decrypt ===
        Cipher cipher = Cipher.getInstance("AES/CBC/PKCS5Padding");
        SecretKeySpec key = new SecretKeySpec(keyBytes, "AES");
        IvParameterSpec iv = new IvParameterSpec(keyBytes);

        cipher.init(Cipher.DECRYPT_MODE, key, iv);

        byte[] decrypted = cipher.doFinal(
                Base64.getDecoder().decode(encryptedBase64)
        );

        // === 5. Output ===
        String plaintext = new String(decrypted, StandardCharsets.UTF_8);
        System.out.println(plaintext);
    }
}
